CREATE TABLE collaboration_data(  id bigserial NOT NULL,  message character varying(65535) NOT NULL,  id_person bigint,  id_collaboration bigint,  "timestamp" integer NOT NULL,  CONSTRAINT collaboration_data_pkey PRIMARY KEY (id),  CONSTRAINT collaboration_data_id_collaboration_fkey FOREIGN KEY (id_collaboration)      REFERENCES collaboration (id) MATCH SIMPLE      ON UPDATE NO ACTION ON DELETE NO ACTION,  CONSTRAINT collaboration_data_id_person_fkey FOREIGN KEY (id_person)      REFERENCES person (id) MATCH SIMPLE      ON UPDATE NO ACTION ON DELETE NO ACTION)