CREATE TABLE collaboration_person_role(  id bigserial NOT NULL,  id_collaboration bigint,  id_person bigint,  id_role bigint,  CONSTRAINT collaboration_person_role_pkey PRIMARY KEY (id),  CONSTRAINT collaboration_person_role_id_collaboration_fkey FOREIGN KEY (id_collaboration)      REFERENCES collaboration (id) MATCH SIMPLE      ON UPDATE NO ACTION ON DELETE NO ACTION,  CONSTRAINT collaboration_person_role_id_person_fkey FOREIGN KEY (id_person)      REFERENCES person (id) MATCH SIMPLE      ON UPDATE NO ACTION ON DELETE NO ACTION,  CONSTRAINT collaboration_person_role_id_role_fkey FOREIGN KEY (id_role)      REFERENCES collaboration_role (id) MATCH SIMPLE      ON UPDATE NO ACTION ON DELETE NO ACTION)